import React from "react";
import IMG_PLACEHOLDER from "../../images/IMAGE_NOT_FOUND.jpg";

const Cast = ({ cast }) => {
  return (
    <div>
      {cast.map(({ person, character, voice }, key) => (
        <div key={key}>
          <div>
            <img
              src={person.image ? person.image.medium : IMG_PLACEHOLDER}
              alt="cast-person"
            />
          </div>
          <div>
            <span>
              {person.name} | {character.name} {voice ? "| Voice" : ""}
            </span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Cast;
